(function() {
  var Postmaster, initWindow, scriptContent;

  Postmaster = require("../main");

  scriptContent = function() {
    var fn;
    fn = function() {
      var pm;
      pm = Postmaster();
      pm.echo = function(value) {
        return value;
      };
      pm.throws = function() {
        throw new Error("This always throws");
      };
      return pm.promiseFail = function() {
        return Promise.reject(new Error("This is a failed promise"));
      };
    };
    return "var module = {};\nPostmaster = " + PACKAGE.distribution.main.content + ";\n(" + (fn.toString()) + ")();";
  };

  initWindow = function(targetWindow) {
    return targetWindow.document.write("<script>" + (scriptContent()) + "<\/script>");
  };

  describe("Postmaster", function() {
    it("should work with openened windows", function(done) {
      var childWindow, postmaster;
      childWindow = open("", null, "width=200,height=200");
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      return postmaster.invokeRemote("echo", 5).then(function(result) {
        return assert.equal(result, 5);
      }).then(function() {
        return done();
      }, function(error) {
        return done(error);
      }).then(function() {
        return childWindow.close();
      });
    });
    it("should work with iframes", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      return postmaster.invokeRemote("echo", 17).then(function(result) {
        return assert.equal(result, 17);
      }).then(function() {
        return done();
      }, function(error) {
        return done(error);
      }).then(function() {
        return iframe.remove();
      });
    });
    it("should handle the remote call throwing errors", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      return postmaster.invokeRemote("throws")["catch"](function(error) {
        return done();
      }).then(function() {
        return iframe.remove();
      });
    });
    it("should throwing a useful error when the remote doesn't define the function", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      return postmaster.invokeRemote("someUndefinedFunction")["catch"](function(error) {
        return done();
      }).then(function() {
        return iframe.remove();
      });
    });
