        return childWindow;
      };
      return postmaster.invokeRemote("promiseFail")["catch"](function(error) {
        return done();
      }).then(function() {
        return iframe.remove();
      });
    });
    it("should be able to go around the world", function(done) {
      var childWindow, iframe, postmaster;
      iframe = document.createElement('iframe');
      document.body.appendChild(iframe);
      childWindow = iframe.contentWindow;
      initWindow(childWindow);
      postmaster = Postmaster();
      postmaster.remoteTarget = function() {
        return childWindow;
      };
      postmaster.yolo = function(txt) {
        return "heyy " + txt;
